import io.restassured.RestAssured;import io.restassured.builder.RequestSpecBuilder;import io.restassured.filter.log.RequestLoggingFilter;import io.restassured.http.ContentType;import org.junit.Before;import org.junit.Test;import static io.restassured.RestAssured.given;import static org.hamcrest.Matchers.*;public class ReqResTests {    @Before    public void setup() {        RestAssured.baseURI = "https://reqres.in";        RestAssured.basePath = "/api";        RestAssured.filters(new RequestLoggingFilter(), new RequestLoggingFilter());        RestAssured.requestSpecification = new RequestSpecBuilder()                .setContentType(ContentType.JSON)                .build();    }    @Test    public void loginTest() {        given()                .contentType(ContentType.JSON)                .body("{\n" +                        "    \"email\": \"eve.holt@reqres.in\",\n" +                        "    \"password\": \"cityslicka\"\n" +                        "}")                .post("/login")                .then()                .statusCode(200)                .body("token", notNullValue());    }    @Test    public void getSingleUserTest() {        given()                .contentType(ContentType.JSON)                .get("/users/2")                .then()                .statusCode(200)                .body("data.id", equalTo(2));    }    @Test    public void deleteUserTest() {        given()                .delete("/users/2")                .then()                .statusCode(204);    }    @Test    public void patchUserTest() {       given()                .when()                .body("{\n" +                        "    \"name\": \"morpheus\",\n" +                        "    \"job\": \"zion resident\"\n" +                        "}")                .patch("/users/2")                .then()                .statusCode(200);    }}